mapper.insert:
insert into review_pic ( review_pic_no, review_no, review_pic_src, review_pic_size ) select review_pic_no.nextval as review_pic_no, T.* from (
select #{item.review_no} as review_no, #{item.review_pic_src} as review_pic_src, #{item.review_pic_size} as review_pic_size from sys.dual
) T

mapper.update:
update review set
review_content = #{review_content}, review_update_date = sysdate where
review_no = #{review_no}
